const state = {
  show: false,
  message: '',
  type: 'success',
  timer: null
}

const mutations = {
  SHOW_MESSAGE(state, { message, type = 'success' }) {
    state.show = true
    state.message = message
    state.type = type
  },
  HIDE_MESSAGE(state) {
    state.show = false
  },
  SET_TIMER(state, timer) {
    state.timer = timer
  }
}

const actions = {
  showMessage({ commit, state }, { message, type = 'success', duration = 3000 }) {
    // 清除之前的定时器
    if (state.timer) {
      clearTimeout(state.timer)
    }
    
    // 显示新消息
    commit('SHOW_MESSAGE', { message, type })
    
    // 设置新的定时器
    const timer = setTimeout(() => {
      commit('HIDE_MESSAGE')
    }, duration)
    
    commit('SET_TIMER', timer)
  }
}

export default {
  namespaced: true,
  state,
  mutations,
  actions
}
