// API 基础配置
const isDevelopment = process.env.NODE_ENV === 'development';

// 根据环境设置基础 URL
const baseURL = isDevelopment 
  ? 'http://localhost:5000/api'  // 开发环境
  : 'https://api.ecopaw.com';    // 生产环境

// API 端点
export const API_URLS = {
  login: '/auth/login',
  logout: '/auth/logout',
  register: '/auth/register',
  captcha: '/auth/captcha',
  profile: '/user/profile',
  calculator: '/carbon/calculate',
  pet: '/pet',
  achievements: '/achievement',
  ranking: '/leaderboard'
};

// axios 配置
export const axiosConfig = {
  baseURL,
  timeout: 15000,
  withCredentials: true,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json, image/svg+xml'
  }
};

// 请求拦截器配置
export const requestInterceptor = (config) => {
  // 如果是获取验证码的请求，修改 headers
  if (config.url === API_URLS.captcha) {
    config.headers['Accept'] = 'image/svg+xml';
    config.responseType = 'text';
    return config;
  }
  
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
};

// 响应拦截器配置
export const responseInterceptor = {
  success: (response) => response,
  error: (error) => {
    if (error.response) {
      // 处理401错误（未授权）
      if (error.response.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('username');
        if (window.location.pathname !== '/login') {
          window.location.href = '/login';
        }
      }
    }
    return Promise.reject(error);
  }
};
