// src/router/index.js
import { createRouter, createWebHistory } from 'vue-router'
import Home from '@/views/Home.vue'

const routes = [
  {
    path: '/',
    name: 'Home',
    component: Home
  },
  {
    path: '/calculator',
    name: 'CarbonCalculator',
    component: () => import('@/views/CarbonCalculatorPage.vue'),
    meta: { requiresAuth: false }
  },
  {
    path: '/carbon-reduction',
    name: 'CarbonReduction',
    component: () => import('@/views/CarbonReductionPage.vue'),
    meta: { requiresAuth: false }
  },
  {
    path: '/pet',
    name: 'VirtualPet',
    component: () => import('@/views/VirtualPetPage.vue'),
    meta: { requiresAuth: true }
  },
  {
    path: '/achievements',
    name: 'Achievements',
    component: () => import('@/views/AchievementsPage.vue'),
    meta: { requiresAuth: true }
  },
  {
    path: '/ranking',
    name: 'Ranking',
    component: () => import('@/views/LeaderboardPage.vue'),
    meta: { requiresAuth: true }
  },
  {
    path: '/login',
    name: 'Login',
    component: () => import('@/views/Login.vue')
  },
  {
    path: '/register',
    name: 'Register',
    component: () => import('@/views/Register.vue')
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

// 导航守卫
router.beforeEach((to, from, next) => {
  const isAuthenticated = !!localStorage.getItem('token')
  
  // 明确检查是否是碳计算器或减碳计算器路由
  const publicPaths = ['/', '/calculator', '/carbon-reduction', '/login', '/register']
  if (publicPaths.includes(to.path)) {
    // 允许访问公共路径
    next()
    return
  }
  
  // 对于需要认证的路由，检查meta.requiresAuth
  if (to.matched.some(record => record.meta.requiresAuth)) {
    if (!isAuthenticated) {
      next({
        path: '/login',
        query: { redirect: to.fullPath }
      })
    } else {
      next()
    }
  } else {
    next()
  }
})

export default router
